        this.token = this.auth
            .getAccessTokenSilently()
            .pipe()
            .subscribe((user) => (this.token = user));
        console.log(this.token);
    }
    async fetchQueets() {
        this.token = this.auth
            .getAccessTokenSilently()
            .pipe()
            .subscribe((user) => (this.token = user));
        return this.httpClient.get(baseUrl, {
            headers: {
                Authorization: `Bearer ${this.token}`,
            },
        });
    }
    postQueet(queet) {
        console.log(queet);
        return this.httpClient.post(baseUrl, queet, httpOptions);
    }
}
