import { __decorate } from "tslib";
import { Component } from '@angular/core';
import { first } from 'rxjs';
let ProfileComponent = class ProfileComponent {
    constructor(auth, queetService, route, userService) {
        this.auth = auth;
        this.queetService = queetService;
        this.route = route;
        this.userService = userService;
        this.profileJson = '';
    }
    async ngOnInit() {
        this.auth.user$.subscribe((profile) => (this.authUser = profile.sub));
        await this.userService
            .fetchUserByUsername(this.route.snapshot.params['username'])
            .pipe(first())
            .subscribe((val) => {
            this.user = val;
            this.queetService
                .fetchQueetsByUserId(this.user['id'])
                .pipe(first())
                .subscribe((val) => (this.queets = val));
        });
        // await this.queetService
        //   .fetchQueetsByUserId(this.user['id'])
        //   .pipe(first())
        //   .subscribe((val) => (this.queets = val));
        console.log(this.route.snapshot.params['username']);
        console.log(this.authUser);
    }
    followUser() {
        console.log('followed user');
    }
};
ProfileComponent = __decorate([
    Component({
        selector: 'app-profile',
        templateUrl: './profile.component.html',
        styleUrls: ['./profile.component.css'],
    })
], ProfileComponent);
export { ProfileComponent };
